  return (
    <div className="App">
      <h1>Employee Management System</h1>
      <EmployeeForm
        onSubmit={handleAddOrEdit}
        selectedEmployee={employees[selectedEmployeeIndex]}
      />
      <EmployeeList
        employees={employees}
        onDelete={handleDelete}
        onEdit={handleEdit}
      />
    </div>
  );
}

export default App;
